func (o *Circle) Preload(name string, retrieved any) error {
	if o == nil {
		return nil
	}

	switch name {
	case "CircleInvitations":
		rels, ok := retrieved.(CircleInvitationSlice)
		if !ok {
			return fmt.Errorf("circle cannot load %T as %q", retrieved, name)
		}

		o.R.CircleInvitations = rels

		for _, rel := range rels {
			if rel != nil {
				rel.R.Circle = o
			}
		}
		return nil
	case "CircleMembers":
		rels, ok := retrieved.(CircleMemberSlice)
		if !ok {
			return fmt.Errorf("circle cannot load %T as %q", retrieved, name)
		}

		o.R.CircleMembers = rels

		for _, rel := range rels {
			if rel != nil {
				rel.R.Circle = o
			}
		}
		return nil
	case "CircleSharingPreferences":
		rels, ok := retrieved.(CircleSharingPreferenceSlice)
		if !ok {
			return fmt.Errorf("circle cannot load %T as %q", retrieved, name)
		}

		o.R.CircleSharingPreferences = rels

		for _, rel := range rels {
			if rel != nil {
				rel.R.Circle = o
			}
		}
		return nil
	case "CreatedByUser":
		rel, ok := retrieved.(*User)
		if !ok {
			return fmt.Errorf("circle cannot load %T as %q", retrieved, name)
		}

		o.R.CreatedByUser = rel

		if rel != nil {
			rel.R.CreatedByCircles = CircleSlice{o}
		}
		return nil
	case "Conversations":
		rels, ok := retrieved.(ConversationSlice)
		if !ok {
			return fmt.Errorf("circle cannot load %T as %q", retrieved, name)
		}

		o.R.Conversations = rels

		for _, rel := range rels {
			if rel != nil {
				rel.R.Circle = o
			}
		}
		return nil
	default:
		return fmt.Errorf("circle has no relationship %q", name)
	}
}

type circlePreloader struct {
	CreatedByUser func(...psql.PreloadOption) psql.Preloader
}

func buildCirclePreloader() circlePreloader {
	return circlePreloader{
		CreatedByUser: func(opts ...psql.PreloadOption) psql.Preloader {
			return psql.Preload[*User, UserSlice](psql.PreloadRel{
				Name: "CreatedByUser",
				Sides: []psql.PreloadSide{
					{
						From:        Circles,
						To:          Users,
						FromColumns: []string{"created_by"},
						ToColumns:   []string{"id"},
					},
				},
			}, Users.Columns.Names(), opts...)
		},
	}
}

