// LoadUser loads the messageDeletion's User into the .R struct
func (o *MessageDeletion) LoadUser(ctx context.Context, exec bob.Executor, mods ...bob.Mod[*dialect.SelectQuery]) error {
	if o == nil {
		return nil
	}

	// Reset the relationship
	o.R.User = nil

	related, err := o.User(mods...).One(ctx, exec)
	if err != nil {
		return err
	}

	related.R.MessageDeletions = MessageDeletionSlice{o}

	o.R.User = related
	return nil
}

// LoadUser loads the messageDeletion's User into the .R struct
func (os MessageDeletionSlice) LoadUser(ctx context.Context, exec bob.Executor, mods ...bob.Mod[*dialect.SelectQuery]) error {
	if len(os) == 0 {
		return nil
	}

	users, err := os.User(mods...).All(ctx, exec)
	if err != nil {
		return err
	}

	for _, o := range os {
		if o == nil {
			continue
		}

		for _, rel := range users {

			if !(o.UserID == rel.ID) {
				continue
			}

			rel.R.MessageDeletions = append(rel.R.MessageDeletions, o)

			o.R.User = rel
			break
		}
	}

	return nil
}

type messageDeletionJoins[Q dialect.Joinable] struct {
	typ     string
	Message modAs[Q, messageColumns]
	User    modAs[Q, userColumns]
}

func (j messageDeletionJoins[Q]) aliasedAs(alias string) messageDeletionJoins[Q] {
	return buildMessageDeletionJoins[Q](buildMessageDeletionColumns(alias), j.typ)
}

func buildMessageDeletionJoins[Q dialect.Joinable](cols messageDeletionColumns, typ string) messageDeletionJoins[Q] {
	return messageDeletionJoins[Q]{
		typ: typ,
		Message: modAs[Q, messageColumns]{
			c: Messages.Columns,
			f: func(to messageColumns) bob.Mod[Q] {
				mods := make(mods.QueryMods[Q], 0, 1)

				{
					mods = append(mods, dialect.Join[Q](typ, Messages.Name().As(to.Alias())).On(
						to.ID.EQ(cols.MessageID),
					))
				}

				return mods
			},
		},
		User: modAs[Q, userColumns]{
			c: Users.Columns,
			f: func(to userColumns) bob.Mod[Q] {
				mods := make(mods.QueryMods[Q], 0, 1)

				{
					mods = append(mods, dialect.Join[Q](typ, Users.Name().As(to.Alias())).On(
						to.ID.EQ(cols.UserID),
					))
				}

				return mods
			},
		},
	}
}
