// LoadMessage loads the messageReceipt's Message into the .R struct
func (o *MessageReceipt) LoadMessage(ctx context.Context, exec bob.Executor, mods ...bob.Mod[*dialect.SelectQuery]) error {
	if o == nil {
		return nil
	}

	// Reset the relationship
	o.R.Message = nil

	related, err := o.Message(mods...).One(ctx, exec)
	if err != nil {
		return err
	}

	related.R.MessageReceipts = MessageReceiptSlice{o}

	o.R.Message = related
	return nil
}

// LoadMessage loads the messageReceipt's Message into the .R struct
func (os MessageReceiptSlice) LoadMessage(ctx context.Context, exec bob.Executor, mods ...bob.Mod[*dialect.SelectQuery]) error {
	if len(os) == 0 {
		return nil
	}

	messages, err := os.Message(mods...).All(ctx, exec)
	if err != nil {
		return err
	}

	for _, o := range os {
		if o == nil {
			continue
		}

		for _, rel := range messages {

			if !(o.MessageID == rel.ID) {
				continue
			}

			rel.R.MessageReceipts = append(rel.R.MessageReceipts, o)

			o.R.Message = rel
			break
		}
	}

	return nil
}

