type messageReceiptJoins[Q dialect.Joinable] struct {
	typ     string
	Message modAs[Q, messageColumns]
	User    modAs[Q, userColumns]
}

func (j messageReceiptJoins[Q]) aliasedAs(alias string) messageReceiptJoins[Q] {
	return buildMessageReceiptJoins[Q](buildMessageReceiptColumns(alias), j.typ)
}

func buildMessageReceiptJoins[Q dialect.Joinable](cols messageReceiptColumns, typ string) messageReceiptJoins[Q] {
	return messageReceiptJoins[Q]{
		typ: typ,
		Message: modAs[Q, messageColumns]{
			c: Messages.Columns,
			f: func(to messageColumns) bob.Mod[Q] {
				mods := make(mods.QueryMods[Q], 0, 1)

				{
					mods = append(mods, dialect.Join[Q](typ, Messages.Name().As(to.Alias())).On(
						to.ID.EQ(cols.MessageID),
					))
				}

				return mods
			},
		},
		User: modAs[Q, userColumns]{
			c: Users.Columns,
			f: func(to userColumns) bob.Mod[Q] {
				mods := make(mods.QueryMods[Q], 0, 1)

				{
					mods = append(mods, dialect.Join[Q](typ, Users.Name().As(to.Alias())).On(
						to.ID.EQ(cols.UserID),
					))
				}

				return mods
			},
		},
	}
}
